
public class JoinMethod {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String Date=String.join("/", "11","12","2020");
		String Time=String.join(":", "1","04","07");
		System.out.println(Date); // 11/12/2020
		System.out.println(Time); // 1:04:07 // ""+time insted of Time

	}

}
